export const initialState = {
    isLoggedin: false,
    isLoggingin: false,
    isLoggingout: false,
    isSigningup: false,
    myInfo: null,
    signupData: {},
    loginData: {},
};

// redux-thunk
// export const loginAction = (data) => {
//     return (dispatch, getState) => {
//         dispatch(loginRequestAction());
//         axios
//             .post('api/login')
//             .then((res) => {
//                 dispatch(loginSuccessAction(res.data));
//             })
//             .catch((err) => {
//                 dispatch(loginFailureAction(err));
//             });
//     };
// };

export const loginRequestAction = (data) => {
    return {
        type: LOGIN_REQUEST,
        data,
    };
};

export const logoutRequestAction = () => {
    return {
        type: LOGOUT_REQUEST,
    };
};

export const followRequestAction = () => {
    return {
        type: FOLLOW_REQUEST,
    };
};

export const unfollowRequestAction = () => {
    return {
        type: UNFOLLOW_REQUEST,
    };
};

export const signupRequestAction = (data) => {
    return {
        type: SIGNUP_REQUEST,
        data,
    };
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN_REQUEST:
            return {
                ...state,
                isLoggingin: true,
            };
        case LOGIN_SUCCESS:
            return {
                ...state,
                isLoggingin: false,
                isLoggedin: true,
                myInfo: action.data,
            };
        case LOGIN_FAILURE:
            return {
                ...state,
                isLoggingin: false,
            };
        case LOGOUT_REQUEST:
            return {
                ...state,
                isLoggingout: true,
            };
        case LOGOUT_SUCCESS:
            return {
                ...state,
                isLoggingout: false,
                isLoggedin: false,
                myInfo: null,
            };
        case LOGOUT_FAILURE:
            return {
                ...state,
                isLoggingout: false,
            };
        case SIGNUP_REQUEST:
            return {
                ...state,
                isSigningup: true,
            };
        case SIGNUP_SUCCESS:
            return {
                ...state,
                isSigningup: false,
            };
        case SIGNUP_FAILURE:
            return {
                ...state,
                isSigningup: false,
            };
        default:
            return state;
    }
};

export default reducer;
