import { Form, Input, Button } from 'antd';
import Link from 'next/link';
import { useCallback, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import useInput from '../hooks/useInput';
import { loginRequestAction } from '../reducers/user';

const ButtonWrapper = styled.div`
    margin-top: 10px;
`;

const LoginForm = () => {
    const [email, onChangeEmail] = useInput('');
    const [passwd, onChangePasswd] = useInput('');
    const { loginLoading } = useSelector((state) => state.user);

    const dispatch = useDispatch();

    const onSubmitLoginForm = useCallback(() => {
        console.log(email, passwd);
        dispatch(
            loginRequestAction({
                email,
                passwd,
            })
        );
    }, [id, passwd]);

    return (
        <Form onFinish={onSubmitLoginForm}>
            <div>
                <label htmlFor="user-emial">아이디</label>
                <br />
                <Input name="user-email" value={email} onChange={onChangeEmail} required />
            </div>
            <div>
                <label htmlFor="user-passwd">비밀번호</label>
                <br />
                <Input name="user-passwd" value={passwd} onChange={onChangePasswd} required />
            </div>
            <ButtonWrapper>
                <Button type="primary" htmlType="submit" loading={loginLoading}>
                    로그인
                </Button>
                <Link href="/signup">
                    <a>
                        <Button>Signup</Button>
                    </a>
                </Link>
            </ButtonWrapper>
        </Form>
    );
};

export default LoginForm;
