import PropTypes from 'prop-types';
import { Button, Form, Input } from 'antd';
import { useCallback, useEffect } from 'react';
import useInput from '../hooks/useInput';
import { useDispatch, useSelector } from 'react-redux';
import { addCommentRequestAction } from '../reducers/post';

const CommentForm = ({ post }) => {
    const [commentText, onChangeCommentText, setInput] = useInput('');
    const id = useSelector((state) => state.user.me?.id);
    const dispatch = useDispatch();

    useEffect(() => {
        if (addPostDone) {
            setText('');
        }
    }, [addPostDone]);

    const onSubmit = useCallback(() => {
        console.log(post.id, commentText);
        dispatch(
            addCommentRequestAction({
                content: commentText,
                postId: post.id,
                userId: id,
            })
        );
    }, [commentText]);

    return (
        <Form onFinish={onSubmit}>
            <Form.Item>
                <Input.TextArea style={{ marginBottom: 5 }} placeholder="댓글을 입력해주세요." value={commentText} onChange={onChangeCommentText} rows={4} />
                <Button style={{ float: 'right' }} type="primary" htmlType="submit">
                    comment
                </Button>
            </Form.Item>
        </Form>
    );
};

CommentForm.propTypes = {
    post: PropTypes.object.isRequired,
};

export default CommentForm;
