import {
    FOLLOW_REQUEST,
    LOGIN_FAILURE,
    LOGIN_REQUEST,
    LOGIN_SUCCESS,
    LOGOUT_FAILURE,
    LOGOUT_REQUEST,
    LOGOUT_SUCCESS,
    SIGNUP_FAILURE,
    SIGNUP_REQUEST,
    SIGNUP_SUCCESS,
    UNFOLLOW_REQUEST,
} from '../actions/user';

export const initialState = {
    loginLoading: false,
    loginDone: false,
    lgoinError: false,
    lgoutLoading: false,
    logoutDone: false,
    logoutError: false,
    signupLoading: false,
    signupDone: false,
    sginupError: false,
    myInfo: null,
    signupData: {},
    loginData: {},
};

export const loginRequestAction = (data) => {
    return {
        type: LOGIN_REQUEST,
        data,
    };
};

export const logoutRequestAction = () => {
    return {
        type: LOGOUT_REQUEST,
    };
};

export const followRequestAction = () => {
    return {
        type: FOLLOW_REQUEST,
    };
};

export const unfollowRequestAction = () => {
    return {
        type: UNFOLLOW_REQUEST,
    };
};

export const signupRequestAction = (data) => {
    return {
        type: SIGNUP_REQUEST,
        data,
    };
};

const dummyGenerator = (data) => ({
    ...data,
    nickname: 'cksdnr3',
    id: 1,
    Posts: [],
    Followings: [],
    Followers: [],
});

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN_REQUEST:
            return {
                ...state,
                loginLoading: true,
                loginError: null,
            };
        case LOGIN_SUCCESS:
            return {
                ...state,
                loginLoading: false,
                loginDone: true,
                myInfo: action.data,
            };
        case LOGIN_FAILURE:
            return {
                ...state,
                loginLoading: false,
                loginError: action.error,
            };
        case LOGOUT_REQUEST:
            return {
                ...state,
                isLoggingout: true,
            };
        case LOGOUT_SUCCESS:
            return {
                ...state,
                isLoggingout: false,
                isLoggedin: false,
                myInfo: null,
            };
        case LOGOUT_FAILURE:
            return {
                ...state,
                isLoggingout: false,
            };
        case SIGNUP_REQUEST:
            return {
                ...state,
                isSigningup: true,
            };
        case SIGNUP_SUCCESS:
            return {
                ...state,
                isSigningup: false,
            };
        case SIGNUP_FAILURE:
            return {
                ...state,
                isSigningup: false,
            };
        default:
            return state;
    }
};

export default reducer;

