import produce from 'immer';
import { v4 } from 'uuid';
import {
  ADD_POST_TO_ME,
  CHANGE_NICKNAME_FAILURE,
  CHANGE_NICKNAME_REQUEST,
  CHANGE_NICKNAME_SUCCESS,
  DELETE_POST_OF_ME,
  FOLLOW_REQUEST,
  LOGIN_FAILURE,
  LOGIN_REQUEST,
  LOGIN_SUCCESS,
  LOGOUT_FAILURE,
  LOGOUT_REQUEST,
  LOGOUT_SUCCESS,
  SIGNUP_FAILURE,
  SIGNUP_REQUEST,
  SIGNUP_SUCCESS,
  UNFOLLOW_REQUEST,
} from '../actions/user';

export const initialState = {
  loginLoading: false,
  loginDone: false,
  lgoinError: null,
  logoutLoading: false,
  logoutDone: false,
  logoutError: null,
  signupLoading: false,
  signupDone: false,
  sginupError: null,
  changeNicknameLoading: false,
  changeNicknameDone: false,
  changeNicknameError: null,
  myInfo: null,
  signupData: {},
  loginData: {},
};

export const loginRequestAction = (data) => ({
  type: LOGIN_REQUEST,
  data,
});

export const logoutRequestAction = () => ({
  type: LOGOUT_REQUEST,
});

export const followRequestAction = () => ({
  type: FOLLOW_REQUEST,
});

export const unfollowRequestAction = () => ({
  type: UNFOLLOW_REQUEST,
});

export const signupRequestAction = (data) => ({
  type: SIGNUP_REQUEST,
  data,
});

const dummyGenerator = (data) => ({
  ...data,
  nickname: 'cksdnr3',
  id: v4(),
  Posts: [],
  Followings: [],
  Followers: [],
});

const reducer = (state = initialState, action) => produce(state, (draft) => {
  switch (action.type) {
    case LOGIN_REQUEST:
      draft.loginLoading = true;
      break;
    case LOGIN_SUCCESS:
      draft.loginLoading = false;
      draft.loginDone = true;
      draft.myInfo = dummyGenerator(action.data);
      break;
    case LOGIN_FAILURE:
      draft.loginLoading = false;
      draft.loginError = action.error;
      break;
    case LOGOUT_REQUEST:
      draft.logoutLoading = true;
      break;
    case LOGOUT_SUCCESS:
      draft.logoutLoading = false;
      draft.loginDone = false;
      draft.myInfo = null;
      break;
    case LOGOUT_FAILURE:
      draft.logoutLoading = false;
      draft.logoutError = action.error;
      break;
    case CHANGE_NICKNAME_REQUEST:
      draft.changeNicknameLoading = true;
      break;
    case CHANGE_NICKNAME_SUCCESS:
      draft.changeNicknameLoading = false;
      draft.changeNicknameDone = false;
      draft.myInfo.nickname = action.data;
      break;
    case CHANGE_NICKNAME_FAILURE:
      draft.changeNicknameLoading = false;
      draft.changeNicknameError = action.error;
      break;
    case SIGNUP_REQUEST:
      draft.signupLoading = true;
      return {
        ...state,
        signupLoading: true,
      };
    case SIGNUP_SUCCESS:
      return {
        ...state,
        signupLoading: false,
      };
    case SIGNUP_FAILURE:
      return {
        ...state,
        signupLoading: false,
      };
    case ADD_POST_TO_ME:
      return {
        ...state,
        myInfo: {
          ...state.myInfo,
          Posts: [{ id: action.data }, ...state.myInfo.Posts],
        },
      };
    case DELETE_POST_OF_ME:
      return {
        ...state,
        myInfo: {
          ...state.myInfo,
          Posts: state.myInfo.Posts.filter((p) => p.id !== action.data),
        },
      };
    default:
      return state;
  }
});

export default reducer;

