const initialState = {
    user: {
        isLoggedin: false,
        user: null,
        signupData: {},
        loginData: {},
    },
    post: {
        mainPosts: [],
    },
};

export const loginAction = (data) => {
    return {
        type: 'LOGIN',
        data,
    };
};

export const logoutAction = () => {
    return {
        type: 'LOGOUT',
    };
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedin: true,
                    user: action.data,
                },
            };
        case 'LOGOUT':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedin: false,
                    user: null,
                },
            };
        default:
            return state;
    }
};

export default rootReducer;
