import { Card } from 'antd';
import Avatar from 'antd/lib/avatar/avatar';
import { Button } from 'antd';
import { useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { logoutRequestAction } from '../reducers/user';

const UserProfile = () => {
    const { myInfo, logoutLoading } = useSelector((state) => state.user);

    const dispatch = useDispatch();
    const onLogout = useCallback(() => {
        dispatch(logoutRequestAction());
    }, []);

    return (
        <Card
            actions={[
                <div key="twit">
                    짹짹 <br /> {myInfo && myInfo.Posts.length}
                </div>,
                <div key="follwings">
                    팔로잉 <br /> {myInfo && myInfo.Followings.length}
                </div>,
                <div key="followers">
                    팔로워 <br /> {myInfo && myInfo.Followers.length}
                </div>,
            ]}
        >
            <Card.Meta avatar={<Avatar>{myInfo.id[0]}</Avatar>} title={myInfo.id} />
            <Button onClick={onLogout} loading={logoutLoading}>
                Logout
            </Button>
        </Card>
    );
};

export default UserProfile;
