export const initialState = {
    isLoggedin: false,
    isLoggingin: false,
    isLoggingout: false,
    myInfo: null,
    signupData: {},
    loginData: {},
};

// redux-thunk
// export const loginAction = (data) => {
//     return (dispatch, getState) => {
//         dispatch(loginRequestAction());
//         axios
//             .post('api/login')
//             .then((res) => {
//                 dispatch(loginSuccessAction(res.data));
//             })
//             .catch((err) => {
//                 dispatch(loginFailureAction(err));
//             });
//     };
// };

export const loginRequestAction = (data) => {
    return {
        type: 'LOGIN_REQUEST',
        data,
    };
};
export const loginSuccessAction = (data) => {
    return {
        type: 'LOGIN_SUCCESS',
        data,
    };
};
export const loginFailureAction = (data) => {
    return {
        type: 'LOGIN_FAILURE',
        data,
    };
};

export const logoutRequestAction = () => {
    return {
        type: 'LOGOUT_REQUEST',
    };
};
export const logoutSuccessAction = () => {
    return {
        type: 'LOGOUT_SUCCESS',
    };
};
export const logoutFailureAction = () => {
    return {
        type: 'LOGOUT_FAILURE',
    };
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN_REQUEST':
            return {
                ...state,
                isLoggingin: true,
            };
        case 'LOGIN_SUCCESS':
            return {
                ...state,
                isLoggingin: false,
                isLoggedin: true,
                myInfo: action.data,
            };
        case 'LOGIN_FAILURE':
            return {
                ...state,
                isLoggingin: false,
            };
        case 'LOGOUT_REQUEST':
            return {
                ...state,
                isLoggingout: true,
            };
        case 'LOGOUT_SUCCESS':
            return {
                ...state,
                isLoggingout: false,
                isLoggedin: false,
                myInfo: null,
            };
        case 'LOGOUT_FAILURE':
            return {
                ...state,
                isLoggingout: false,
            };
        default:
            return state;
    }
};

export default reducer;
